mapper.select:
	select * from news_comment
	where topic_id = #{newsid}
	and topic_type = #{topic_type}
	and commentstate='1'


    limit #{pageNum}, #{pageSize}

select

from news_comment

    and date(time) >= #{start} 
and #{end} >= date(time)

     and commentstate = #{commentstate}



 order by time desc


  limit #{pageNum}, #{pageSize}

select count(id) as count
from news_comment

 and date(news_comment.time) >= #{start} 
and #{end} >= date(news_comment.time)

    and commentstate = #{commentstate}

  select * from news_comment
	where topic_id = #{newsid}
	and from_uid = #{uid}
	and topic_type = #{topic_type}
	and commentstate='0'


    limit #{pageNum}, #{pageSize}

