mapper.select:
select * from t_topic where is_del=0
AND uid = #{userid,jdbcType=VARCHAR}
order by create_time desc
limit #{pageNum}, #{pageSize}

select * from t_topic where is_del=0
AND title LIKE '%${sname}%'
AND content LIKE '%${sprofile}%'
AND uid = #{userid,jdbcType=VARCHAR}
order by create_time desc
limit #{pageNum}, #{pageSize}

SELECT b.id ,b.name,b.photo,c.sname ,c.type,a.* FROM t_topic as a,sys_user as b,t_session as c where a.is_del=0 and a.uid=b.id and c.sid=a.ssession and a.is_top=1 and a.status=0
and a.uid=#{uid}
order by update_time desc limit 0, 5

SELECT b.id ,b.name,b.photo,c.sname ,c.type,a.* FROM t_topic as a,sys_user as b,t_session as c where a.is_del=0 and a.uid=b.id and c.sid=a.ssession and a.is_top=1 and a.status=0
and a.uid=#{uid}
order by update_time desc limit 0, 5

SELECT b.id ,b.name,b.photo,c.sname ,c.type,a.* FROM  t_topic as a,sys_user as b,t_session as c
where a.is_del=0
and a.uid=b.id
and c.sid=a.ssession
and a.status=0

  and a.ssession=#{ssession,jdbcType=VARCHAR}


  and a.title LIKE concat(concat("%",#{title}),"%")


  and a.uid=#{uid}


  limit #{pageNum}, #{pageSize}

SELECT b.id ,b.name,b.photo,c.sname ,c.type,a.* FROM  t_topic as a,sys_user as b,t_session as c
where a.is_del=0
and a.uid=b.id
and c.sid=a.ssession
and a.status=0

    and a.ssession=#{ssession,jdbcType=VARCHAR}


    and a.title LIKE concat(concat("%",#{title}),"%")


    and a.uid=#{uid}


    limit #{pageNum}, #{pageSize}

SELECT count(*) FROM t_topic as a,sys_user as b,t_session as c where a.is_del=0 and a.uid=b.id and c.sid=a.ssession and a.status=0
and a.ssession=#{ssession,jdbcType=VARCHAR}
and a.title LIKE concat(concat("%",#{title}),"%")
and a.uid=#{uid}
